let numbers f t =
  if f > t then failwith "numbers";
  let rec iter i acc =
    let acc = i::acc in
    if i = f then acc
    else iter (i-1) acc
  in
  iter t []

let mult = List.fold_left ( * ) 1

let greatest_product str digits =
  let len = String.length str in
  let indices = numbers 0 (digits - 1) in
  let rec iter i acc =
    if i + digits >= len then acc
    else
      let n =
        mult (List.map
                (fun idx -> int_of_char (str.[i+idx]) - int_of_char '0')
                indices)
      in
      iter (i+1) (max acc n)
  in
  iter 0 0

let _ =
  let str = "" in
  greatest_product str 5
